import React from 'react';
import classes from "./SearchBar.module.css";

const SearchBar = ({str, setStr}) => {

    return (
        <div className={classes.searchBox}>

            <input style={{width: str ? '240px' : ''}} className={classes.searchInput} type="text" name="" placeholder="Search" onChange={(e)=>setStr(e.target.value)}/>
            <button className={classes.searchButton}>
                <svg width="32px" height="32px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path d="M15.7955 15.8111L21 21M18 10.5C18 14.6421 14.6421 18 10.5 18C6.35786 18 3 14.6421 3 10.5C3 6.35786 6.35786 3 10.5 3C14.6421 3 18 6.35786 18 10.5Z" stroke="#fff" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"></path> </g></svg>
            </button>
        </div>
    );
};

export default SearchBar;
