import React, {useContext, useState} from 'react';
import classes from "./LoginForm.module.css"
import {Context} from "../../../context";
import {useFetching} from "../../../hooks/useFetching";
import NewsService from "../../../API/NewsService";
import UserService from "../../../API/UserService";
import {useNavigate} from "react-router-dom";

const LoginForm = () => {
    // const [userName, setUserName] = useState('');
    // const [password, setPassword] = useState('');
    const [userForm, setUserForm] = useState({
        username: '',
        password: ''
    });
    const { setIsAuth } = useContext(Context);
    const { user, setUser } = useContext(Context);
    const router = useNavigate();
    // const login = async (e) => {
    //     e.preventDefault();
        // try {
        //     const response = await axios.post('http://192.168.0.53:8000/auth/token/login/', {
        //             username: userName,
        //             password: password
        //         },
        //         /*{
        //             headers: {
        //                 'X-CSRFTOken': 'uhTD3vSjaOhnEC2Bda0bgRW5rzvFMTCRnQoX2aRDP2RDHMtP2YEqhaHDQvMbb9h0',
        //                 'Content-Type': 'application/json'
        //             }
        //         }*/
        //     );
        //     console.log(response.data);
        //     console.log('Success');
        //     setIsAuth(true);
            // console.log("aith");
            // sessionStorage.setItem('auth', 'true')
        //     setToken(response.data['auth_token']);
        // } catch (err){
        //     console.error("Error");
        //     console.log(userName);
        //     console.log(password);
        //     console.log(err);
        // }

    // }
    const [login, error] = useFetching(async (e)=>{
        e.preventDefault();
        // console.log(userForm);
        const jsonData = await UserService.login(userForm);
        if(!error){
            user.username = userForm.username;
            user.token = jsonData.token;
            setIsAuth(true);
            // sessionStorage.setItem('auth', 'true');
            sessionStorage.setItem('username', userForm.username);
            sessionStorage.setItem('token', jsonData.token);

            router('/');
        }
        // console.log(jsonData.token);
        // console.log(user);
    });
    function handleChange(e){
        setUserForm({
            ...userForm,
            [e.target.name]: e.target.value
        });
        // console.log(userForm);
    }
    return (
        <form method="post" onSubmit={login}>
            {/*{% csrf_token %}*/}
            <div className={classes.inputbox}>
                {/*<ion-icon name="log-in-outline"></ion-icon>*/}
                <input className={classes.TextInput} name="username" onChange={handleChange} />

                <label style={{ top: userForm.username.length>0 ? -2 : ''}}>Login</label>
            </div>
            <div className={classes.inputbox}>
                <input className={classes.TextInput} name="password" type='password' onChange={handleChange} />

                {/*<ion-icon name="lock-closed-outline"></ion-icon>*/}
                <label style={{ top: userForm.password.length>0 ? -2 : ''}}>Password</label>
            </div>

            <button className={classes.loginButton} type="submit">Log in</button>
            {/*<script type="module" src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.esm.js"></script>*/}
            {/*<script noModule src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.js"></script>*/}
        </form>
    );
};

export default LoginForm;
