import React from 'react';
import classes from "./HotelList.module.css";
import HotelPlate from "../HotelPlate/HotelPlate";
import {useNavigate} from 'react-router-dom'
import Loader from "../../Loader/Loader";
import contextMenu from "../ContextMenu/ContextMenu";

const HotelList = ({Hotels, isLoading}) => {
    const router = useNavigate();
    function ContextMenu(e, id){
        e.preventDefault();
        const a = e.offsetX
        if (contextMenu && contextMenu.contains(e.target)) {
            return;
        }
        let x = e.offsetX, y = e.offsetY,
            winWidth = ad.offsetWidth,
            winHeight = ad.offsetHeight,
            cmWidth = contextMenu.offsetWidth,
            cmHeight = contextMenu.offsetHeight;

//    if(x > (winWidth - cmWidth - shareMenu.offsetWidth)) {
//        shareMenu.style.left = "-200px";
//    } else {
//        shareMenu.style.left = "";
//        shareMenu.style.right = "-200px";
//    }
        x = x > winWidth - cmWidth ? winWidth - cmWidth - 5 : x;
        y = y > winHeight - cmHeight ? winHeight - cmHeight - 5 : y;
        console.log("winWidth = " + winWidth);
        console.log("cmWidth = " + cmWidth);
        contextMenu.style.left = `${x}px`;
        contextMenu.style.top = `${y}px`;
        contextMenu.style.visibility = "visible";
    }
    return (
        isLoading
            ?<Loader/>
            :Hotels.length === 0
                ?
            <label>No Hotels</label>
                :
            <div className={classes.hotels}>
                {Hotels.map((ht)=>
                    <HotelPlate key={ht._id} item={ht} onClick={(e)=>ContextMenu(e, ht._id)}/>
                )}
                {/*{Hotels.map((ht)=>*/}
                {/*    <HotelPlate key={ht._id} item={ht} onClick={()=>router(`/hotel/${ht._id}`)}>*/}
                {/*)}*/}
            </div>


    );
};

export default HotelList;
