import React, {useContext, useState} from 'react';
import classes from './NewsPage.module.css'
import Menu from "../../components/Menu Components/Menu/Menu";
import NewsList from "../../components/News Components/NewsList/NewsList";
import MainLabel from "../../components/MainLabel/MainLabel";
import {Context} from "../../context";
import ModalWindow from "../../components/Modal Window/ModalWindow";
import NewsBar from "../../components/News Components/NewsBar/NewsBar";

const NewsPage = () => {
    const { isAuth } = useContext(Context);

    const [form, setForm] = useState(false);

    const news = [
        {id: 1, title: "Title 1", summary: "summarysummarysummarysummary"},
        {id: 2, title: "Title 2", summary: "summarysummarysummarysummary 1"},
        {id: 3, title: "Title 3", summary: "summarysummarysummarysummary"},
        {id: 4, title: "Title 4", summary: "summarysummarysummarysummary"},
        {id: 5, title: "Title 5", summary: "summarysummarysummarysummary"},
    ];
    const [filteredNews, setFilteredNews] = useState(news);


    return (
        <div className={classes.newsPage}>
            <MainLabel>News</MainLabel>
            <NewsBar news={news} setFilteredNews={setFilteredNews} onAddClick={()=>setForm(true)}/>
            <NewsList News={news}/>
            {isAuth
                ? <label>Auth</label>
                : null
            }
            <Menu/>
            <ModalWindow visible={form} setVisible={setForm}>
                <label>Test</label>
                <label>Test</label>
                <label>Test</label>
            </ModalWindow>

        </div>
    );
};

export default NewsPage;
