import React, {useState} from 'react';
import classes from "./LoginForm.module.css"

const LoginForm = () => {
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');
    // const { setIsAuth } = useContext(AuthContext);

    return (
        <form method="post">
            {/*{% csrf_token %}*/}
            <div className={classes.inputbox}>
                {/*<ion-icon name="log-in-outline"></ion-icon>*/}
                <input className={classes.TextInput} onChange={(e) => setUserName(e.target.value)} />

                <label style={{ top: userName.length>0 ? -2 : ''}}>Login</label>
            </div>
            <div className={classes.inputbox}>
                <input className={classes.TextInput} type='password' onChange={(e) => setPassword(e.target.value)} />

                {/*<ion-icon name="lock-closed-outline"></ion-icon>*/}
                <label style={{ top: password.length>0 ? -2 : ''}}>Password</label>
            </div>

            <button className={classes.loginButton} type="submit">Log in</button>
            {/*<script type="module" src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.esm.js"></script>*/}
            {/*<script noModule src="https://unpkg.com/ionicons@5.5.2/dist/ionicons/ionicons.js"></script>*/}
        </form>
    );
};

export default LoginForm;
