import React, {useContext, useState} from 'react';
import classes from './NewsPage.module.css'
import Menu from "../../components/Menu Components/Menu/Menu";
import NewsList from "../../components/News Components/NewsList/NewsList";
import MainLabel from "../../components/MainLabel/MainLabel";
import {Context} from "../../context";
import ModalWindow from "../../components/Modal Window/ModalWindow";
import MenuButton from "../../components/Menu Components/MenuButton/MenuButton";

const NewsPage = () => {
    const { isAuth } = useContext(Context);

    const [form, setForm] = useState(false);

    return (
        <div className={classes.newsPage}>
            <MainLabel>News</MainLabel>
            <button onClick={()=>setForm(true)}>
                Add
            </button>
            <NewsList/>
            {isAuth
                ? <label>Auth</label>
                : null
            }
            <Menu/>
            <ModalWindow visible={form} setVisible={setForm}>
                <label>Test</label>
                <label>Test</label>
                <label>Test</label>
            </ModalWindow>
            <div className="search-add-filter-container">
                <div className="search-bar">
                    <input type="text" className="search-input" placeholder="Search..."/>
                        <button className="search-button">Search</button>
                </div>
                <button className="add-button">Add</button>
                <div className="filter-buttons">
                    <button className="filter-button">Filter 1</button>
                    <button class="filter-button">Filter 2</button>
                    <button class="filter-button">Filter 3</button>
                </div>
            </div>

        </div>
    );
};

export default NewsPage;
