import React, {useState} from 'react';
import classes from "./NewsForm.module.css";
import {useFetching} from "../../../hooks/useFetching";
import NewsService from "../../../API/NewsService";
const NewsForm = ({ setVisible }) => {
    const [newsletter, setNewsletter] = useState({
        // Initialize with your POST data
        title: '',
        summary: '',
        main_part: '',
        // ...
    });
    const [addNews, isLoading, error] = useFetching(async (n)=>{
        await NewsService.postItem(n);
        console.log(n);
        console.log(error);
        setVisible(false);
    });
    function handleChange(e){
        setNewsletter({
            ...newsletter,
            [e.target.name]: e.target.value
        });
    }
    function addNewsletter(e){
        e.preventDefault();
        console.log("newsletter");
        addNews(newsletter);
    }
    return (
        <form className={classes.newsForm} onSubmit={addNewsletter}>
            <input placeholder='Title' name="title" onChange={handleChange} required/>
            <input placeholder='Summary' name="summary" required/>
            <textarea placeholder='Main part' name="main_part" required></textarea>
            <button>Add</button>
        </form>
    );
};

export default NewsForm;
