import React from 'react';
import classes from "./NewsForm.module.css";
const NewsForm = ({ setVisible }) => {
    async function AddNews(e){
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:5000/api/news'); // Replace with your API endpoint
            if (response.ok) {
                const jsonData = await response.json();
                console.log(jsonData);
            } else {
                throw new Error('Request failed');
            }
        } catch (error) {
            // Handle errors
            console.error('Error fetching data:', error);
        }
        setVisible(false);
    }
    return (
        <form className={classes.newsForm}>
            <input placeholder='Title'/>
            <input placeholder='Summary'/>
            <textarea placeholder='Main part'></textarea>
            <button onClick={AddNews}>Add</button>
        </form>
    );
};

export default NewsForm;
