import React, { useContext, Component } from 'react';
import classes from "./OrderPlate.module.css";
import NewsService from "../../../API/NewsService";
import { useFetching } from "../../../hooks/useFetching";
import HotelService from "../../../API/HotelService";
import OrderService from "../../../API/OrderService";
import { Context } from "../../../context";

class OrderPlate extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    render() {
        const { user } = useContext(Context);

        const [deleteOrder, isDeleting, errorDelete] = useFetching(async () => {
            await OrderService.deleteById(this.props.item._id, user.token);
            // await  fetchHotels();
        });

        return (
            <tr onClick={this.props.onClick}>
                <td>{this.props.item.hotel}</td>
                <td>{this.props.item.client}</td>
                <td>{this.props.item.departure_date}</td>
                <td>{new Date(this.props.item.departure_date).toLocaleDateString()}</td>
                <td>
                    <button onClick={deleteOrder}>Delete</button>
                </td>
            </tr>
        );
    }
}

export default OrderPlate;
