import React, {useEffect, useState} from 'react';
import classes from "./NewsBar.module.css";
import SearchBar from "../SearchBar/SearchBar";
import NewsBarButton from "../NewsBarButton/NewsBarButton";

const NewsBar = ({onAddClick, news, setNews}) => {
    const [searchString, setSearchString] = useState('');

    useEffect(() => {
        setNews(news.filter(
            obj => obj.title.toLowerCase().includes(searchString.toLowerCase()) ||
                obj.summary.toLowerCase().includes(searchString.toLowerCase())
        ));
    }, [searchString]);

    const sortAlphabetically = () => {
        const sortedUsers = [...news].sort((a, b) => a.title.localeCompare(b.title));
        setNews(sortedUsers);
    };
    const sortNotAlphabetically = () => {
        const sortedUsers = [...news].sort((a, b) => a.name.localeCompare(b.name));
        setNews(sortedUsers);
    };
    return (
        <div className={classes.newsBar}>
            <NewsBarButton onClick={onAddClick}>
                <svg width="32px" height="32px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path d="M6 12H18M12 6V18" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"></path> </g></svg>
            </NewsBarButton>
            <NewsBarButton onClick={sortAlphabetically}>
                <svg width="32px" height="32px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path d="M12 6V18M12 18L7 13M12 18L17 13" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"></path> </g></svg>
            </NewsBarButton>
            <NewsBarButton onClick={onAddClick}>
                <svg width="32px" height="32px" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg" transform="rotate(180)"><g id="SVGRepo_bgCarrier" stroke-width="0"></g><g id="SVGRepo_tracerCarrier" stroke-linecap="round" stroke-linejoin="round"></g><g id="SVGRepo_iconCarrier"> <path d="M12 6V18M12 18L7 13M12 18L17 13" stroke="#000000" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"></path> </g></svg>
            </NewsBarButton>
            <SearchBar str={searchString} setStr={setSearchString}/>
        </div>
    );
};

export default NewsBar;
