import React, {useContext, useState} from 'react';
import classes from "./NewsForm.module.css";
import {useFetching} from "../../../hooks/useFetching";
import NewsService from "../../../API/NewsService";
import {Context} from "../../../context";
import MyInput from "../../MyInput/MyInput";
import MyTextarea from "../../MyTextarea/MyTextarea";
const NewsForm = ({ setVisible }) => {
    const {user} = useContext(Context);
    const [newsletter, setNewsletter] = useState({
        // Initialize with your POST data
        title: '',
        summary: '',
        main_part: '',
        // ...
    });
    const [imageFile, setImageFile] = useState(null);
    const [addNewsletter, isLoading, error] = useFetching(async (e)=>{
        // e.preventDefault();
        await NewsService.postItem(newsletter, imageFile, user.token);
        // console.log(n);
        // console.log(error);
        setVisible(false);
    });
    function handleChange(e){
        setNewsletter({
            ...newsletter,
            [e.target.name]: e.target.value
        });
    };
    const handleFileChange = (event) => {
        // Update the state with the selected image file
        setImageFile(event.target.files[0]);
        console.log(imageFile);
    };
    return (
        <form className={classes.newsForm} onSubmit={addNewsletter}>
            <MyInput label={'Title'} name="summary" onChange={handleChange} required/>
            <MyInput label={'Summary'} name="title" onChange={handleChange} required/>
            {/*<input placeholder='Title' name="title" onChange={handleChange} required/>*/}
            {/*<input placeholder='Summary' name="summary" onChange={handleChange} required/>*/}
            <MyTextarea label='Main part' name="main_part" onChange={handleChange} required/>
            <textarea placeholder='Main part' name="main_part" onChange={handleChange} required></textarea>
            <input type="file" name='image' onChange={handleFileChange} />
            <img />
            <button>Add</button>
        </form>
    );
};

export default NewsForm;
